import React, { useState, useEffect } from 'react';
import kanban from './kanban';
import './App.css';

const KANBAN_API_URL = 'http://localhost:5000/tickets';

const App = () => {
  const [tickets, setTickets] = useState([]);
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortBy, setSortBy] = useState('priority');

  useEffect(() => {
    // Fetch data from the API
    fetch(KANBAN_API_URL)
      .then((response) => response.json())
      .then((data) => setTickets(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);
// ... (rest of your existing code)

  // Function to group tickets based on the chosen option
  const groupTickets = () => {
    switch (groupingOption) {
      case 'status':
        return groupByStatus();
      case 'user':
        return groupByUser();
      case 'priority':
        return groupByPriority();
      default:
        return [];
    }
  };

  // Function to group tickets by status
  const groupByStatus = () => {
    const groupedByStatus = {};

    tickets.forEach((ticket) => {
      const { status } = ticket;

      if (!groupedByStatus[status]) {
        groupedByStatus[status] = [ticket];
      } else {
        groupedByStatus[status].push(ticket);
      }
    });

    const statusGroups = Object.entries(groupedByStatus).map(([status, ticketsInGroup]) => ({
      status,
      tickets: ticketsInGroup,
    }));

    return statusGroups;
  };

  // Function to group tickets by user
  const groupByUser = () => {
    const groupedByUser = {};

    tickets.forEach((ticket) => {
      const { user } = ticket;

      if (!groupedByUser[user]) {
        groupedByUser[user] = [ticket];
      } else {
        groupedByUser[user].push(ticket);
      }
    });

    const userGroups = Object.entries(groupedByUser).map(([user, ticketsInGroup]) => ({
      user,
      tickets: ticketsInGroup,
    }));

    return userGroups;
  };

  // Function to group tickets by priority
  const groupByPriority = () => {
    const groupedByPriority = {};

    tickets.forEach((ticket) => {
      const { priority } = ticket;

      if (!groupedByPriority[priority]) {
        groupedByPriority[priority] = [ticket];
      } else {
        groupedByPriority[priority].push(ticket);
      }
    });

    const priorityGroups = Object.entries(groupedByPriority).map(([priority, ticketsInGroup]) => ({
      priority: parseInt(priority),
      tickets: ticketsInGroup,
    }));

    return priorityGroups;
  };

  // Function to sort tickets based on the chosen option
  const sortTickets = (groupedTickets) => {
    switch (sortBy) {
      case 'priority':
        return sortByPriority(groupedTickets);
      case 'title':
        return sortByTitle(groupedTickets);
      default:
        return groupedTickets;
    }
  };

  // Function to sort tickets by priority
  const sortByPriority = (groupedTickets) => {
    return groupedTickets.map((group) => ({
      ...group,
      tickets: group.tickets.sort((a, b) => b.priority - a.priority),
    }));
  };

  // Function to sort tickets by title
  const sortByTitle = (groupedTickets) => {
    return groupedTickets.map((group) => ({
      ...group,
      tickets: group.tickets.sort((a, b) => a.title.localeCompare(b.title)),
    }));
  };

  // Handle grouping option change
  const handleGroupingChange = (e) => {
    setGroupingOption(e.target.value);
  };

  // Handle sorting option change
  const handleSortByChange = (e) => {
    setSortBy(e.target.value);
  };

  // Render the Kanban board based on the grouped and sorted tickets
  const groupedAndSortedTickets = sortTickets(groupTickets());



  return (
    <div className="App">
    <div>
      <label>Group By:</label>
      <select value={groupingOption} onChange={handleGroupingChange}>
        <option value="status">Status</option>
        <option value="user">User</option>
        <option value="priority">Priority</option>
      </select>
    </div>
    <div>
      <label>Sort By:</label>
      <select value={sortBy} onChange={handleSortByChange}>
        <option value="priority">Priority</option>
        <option value="title">Title</option>
      </select>
    </div>
    <button onClick={() => alert('Display button clicked!')}>
      Display
    </button>

    <kanban/>

    <kanban groupedAndSortedTickets={groupedAndSortedTickets} />
  </div>
);

  }
  export default App
